<?php
$recherche = "";
$count = 0;
if (isset($_REQUEST["recherche"])) {
    $recherche = $_POST["recherche"];
}

if ($recherche !== "")

    require '../include/configuration.php';

$sql ="SELECT employes.id as id, CONCAT(employes.prenom, ' ',employes.nom) AS nom, usagers.image as image FROM employes 
INNER JOIN usagers ON employes.id = usagers.employes_id
WHERE employes.nom LIKE '$recherche%' || employes.prenom LIKE '$recherche%';";

//$sql = "SELECT id, nom, image FROM employers WHERE nom LIKE '$recherche%'";
$result = $conn->query($sql);

if ($result->rowCount() > 0) {

    echo "<ul class=\"list-group mx-2 mt-1 to_the_top\">";


    while ($row = $result->fetch()) {
        if ($row["image"] == null){
            $image = "";
        }
        else{
            $image = $row["image"];
            $image = base64_encode($image);
        }
        $id = $row["id"];
        $nom = $row["nom"];
        echo "<a href='#' id='$count' onclick=\"myfunction('$id','$nom');\" type='button'   class=\"list-group-item list-group-item-action to_the_top\">";
        echo $nom;
        echo "<img src=\"data:image/png;base64,$image\" class=\"img-list to_the_top\" alt=\"rfid\">";
        echo "</a>";
        $count++;
    }


    echo "</ul>";
}


require '../include/nettoyage.php';






